import { Component } from '@angular/core';
import {CardEventComponent} from "../card-event/card-event.component";
import {map, Observable} from "rxjs";
import {ActivatedRoute} from "@angular/router";
import {Event} from "../models/event.model"

@Component({
  selector: 'app-home-events',
  templateUrl: './home-events.component.html',
  styleUrls: ['./home-events.component.css']
})
export class HomeEventsComponent {
  eventList$: Observable<Event[]> = this._route.data.pipe(map((data)=>data["events"]))

  itemsVisibles: number =3; // Commencez par afficher les 2 premiers éléments

  afficherPlus() {
    this.itemsVisibles += 2; // Augmentez le nombre d'éléments visibles de 2
  }
  constructor(private _route: ActivatedRoute) {
  }
}
<section id="main">

<h1>Évènements plus populaires</h1>
  <button (click)="afficherPlus()">Afficher plus</button>
<div class="card-grid">
  <app-card-event *ngFor="let event of eventList$ | async"  routerLink="/events" [name]=event.name [lieu]="event.lieu"></app-card-event>
</div>

  <button (click)="afficherPlus()">Afficher plus</button>
</section>
