import { Component } from '@angular/core';

@Component({
  selector: 'app-event-detail',
  templateUrl: './event-detail.component.html',
  styleUrls: ['./event-detail.component.css']
})
export class EventDetailComponent {

}
<div class="event-detail-content">
<img class= "resize rounded" src="../assets/posters/organik-affiche.jpg" alt="Affiche">
  <p class="event-title center">Organik</p>
  <p class="localisation uppercase center">Kilomètre 25</p>
  <p class="date uppercase center"><span>17 Sep</span> | <span>23h00-7h00</span></p>
  <p class="event-description center"></p>
</div>
