import {Component, ElementRef, ViewChild} from '@angular/core';
import {ConnexionService} from "../services/connexion.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-connexion',
  templateUrl: './connexion.component.html',
  styleUrls: ['./connexion.component.css']
})
export class ConnexionComponent {
  isLoginTab = true; // Pour suivre l'onglet actif, par défaut c'est l'onglet de connexion
  @ViewChild('loginUsernameInput') loginUsernameInput!: ElementRef;

  constructor(private router: Router, private connexionService : ConnexionService) {
  }
  toggleTab(arg: String) {
    if(arg=="register"){ //login annonce que le clique était sur le bouton connexion/login
      this.isLoginTab = false;
    }
    else{
      this.isLoginTab = true;
    }
  }

  Login(){
    const username = this.loginUsernameInput.nativeElement.value;
    this.connexionService.Login(username);
    this.router.navigate(['/']);
    event?.preventDefault();
  }
}
<div class="form-container">
  <div class="tabs">
    <button class="tab-button primary-button" id="login-tab" (click)="toggleTab('login')">Connexion</button>
    <button class="tab-button primary-button" id="register-tab" (click)="toggleTab('register')">S'inscrire</button>
  </div>
  <div class="tab-content" id="login-content" *ngIf="isLoginTab">
    <form>
      <h2>Connexion</h2>

      <label for="login-username">Nom d'utilisateur :</label>
      <input #loginUsernameInput type="text" id="login-username" class= "input-nav" name="login-username" placeholder="Nom d'utilisateur" required>

      <label for="login-password">Mot de passe :</label>
      <input type="password" id="login-password" class= "input-nav" name="login-password" placeholder="Mot de passe" required>

      <button class="primary-button" (click)="Login()">Se connecter</button>
    </form>
  </div>
  <div class="tab-content" id="register-content" *ngIf="!isLoginTab">
    <form>
      <h2>Inscription</h2>

      <label for="register-username">Nom d'utilisateur :</label>
      <input type="text" id="register-username" class="input-nav" name="register-username" placeholder="Nom d'utilisateur" required>

      <label for="register-mail">Mail :</label>
      <input type="email" id="register-mail" class="input-nav" name="register-mail" placeholder="Mail" required>

      <label for="register-entreprise">Entreprise :</label>
      <input type="text" id="register-entreprise" class="input-nav" name="register-entreprise" placeholder="Entreprise" required>

      <label for="register-description">Description :</label>
      <textarea id="register-description" class="input-nav" name="register-description" placeholder="Description" required></textarea>

      <label for="register-reseaux">Réseaux :</label>
      <input type="text" id="register-reseaux" class="input-nav" name="register-reseaux" placeholder="Réseaux" required>

      <label for="register-affiche">Affiche :</label>
      <input type="text" id="register-affiche" class="input-nav" name="register-affiche" placeholder="Affiche" required>

      <label for="register-organise">Organise :</label>
      <div *ngFor="let organiseItem of [1, 2, 3]">
        <input type="number" [id]="'register-organise-' + organiseItem" [name]="'register-organise-' + organiseItem" class="input-nav" placeholder="Organise">
      </div>

      <label for="register-pdp">Photo de profil :</label>
      <input type="text" id="register-pdp" class="input-nav" name="register-pdp" placeholder="Photo de profil" required>

      <button class="primary-button">S'inscrire</button>
    </form>
  </div>
