import { Component } from '@angular/core';
import {BehaviorSubject, map, Observable} from "rxjs";
import {Event} from "../models/event.model";
import {ActivatedRoute} from "@angular/router";
import {OrgaService} from "../services/orga.service";
import {EventService} from "../services/event.service";

@Component({
  selector: 'app-my-events',
  templateUrl: './my-events.component.html',
  styleUrls: ['./my-events.component.css']
})
export class MyEventsComponent {

  eventList$: BehaviorSubject<Event[]> = new BehaviorSubject<Event[]>([]);
  constructor(private _route: ActivatedRoute, private eventService: EventService) {}

  ngOnInit() {
    this.eventService.findAll().subscribe((events: Event[]) => {
        this.eventList$.next(events);
      }
    );
  }
  onButtonClick(){
    console.log("clique");
  }

}
<section id="main">

  <h1>My Events</h1>
  <div class="column-center">
    <ng-container *ngFor="let event of eventList$ | async; let i = index">
      <button class="round-button" (click)="onButtonClick()">Click</button>
      <app-card-event [id]="event.id" [lieu]="event.lieu" [theme]="event.theme" [id_orga]="event.orga" [name]="event.name" [prix]="event.prix"></app-card-event>
    </ng-container>  
  </div>

</section>
