import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {BehaviorSubject, Observable} from "rxjs";
import {PublicService} from "./public.service";

@Injectable({
  providedIn: 'root'
})
export class ConnexionService {
  private isLoggedIn = new BehaviorSubject<boolean>(false);
  private _username: string = '';
  private ID: number = -1;

  isConnected:Observable<boolean> = this.isLoggedIn.asObservable()

  get username() {
    return this._username;
  }

  constructor(private http: HttpClient, private publicService: PublicService) {
    const storedUsername = localStorage.getItem('username');
    const storedID = localStorage.getItem('id');
    if (storedUsername && storedID) {
      this._username = storedUsername;
      this.ID = parseInt(storedID, 10);
      this.isLoggedIn.next(true);
    }
  }
  Login(value: string) {
    this._username = value;
    this.isLoggedIn.next(true);
    this.publicService.findByPseudo(value).subscribe((data) => {
      localStorage.setItem('id', (data.pseudo).toString());
    });


  }

  Logout() {
    this._username = '';
    this.isLoggedIn.next(false);
    localStorage.removeItem('username');
  }



}
