import { Component } from '@angular/core';

@Component({
  selector: 'app-add-event',
  templateUrl: './add-event.component.html',
  styleUrls: ['./add-event.component.css']
})
export class AddEventComponent {
  orgaId = 0;

  createEvent(){
    const orga_id = localStorage.getItem("id");
    if(orga_id){
      this.orgaId = parseInt(orga_id, 10);
    }
    const newEvent: Event = {
      orga: this.orgaId,
      id_part: 0,
      name: this.eventNameInput.value,
      theme: this.eventThemeInput.value,
      lieu: this.eventLocationInput.value,
      date: this.eventDateInput.value,
      prix: parseFloat(this.eventPriceInput.value), // Utilisation de parseFloat pour convertir en nombre
      description: this.eventDescriptionInput.value,
      affiche: this.eventPosterInput.value,
    };
}

}
<div class="form-container">
  <div class="tab-content" id="event-creation-content">
    <form>
      <h2>Créer votre évènement</h2>

      <label for="event-name">Nom de l'évènement :</label>
      <input #eventNameInput type="text" id="event-name" class="input-nav" name="event-name" placeholder="Nom de l'évènement" required>

      <label for="event-theme">Thème :</label>
      <input #eventThemeInput type="text" id="event-theme" class="input-nav" name="event-theme" placeholder="Thème">

      <label for="event-location">Lieu :</label>
      <input #eventLocationInput type="text" id="event-location" class="input-nav" name="event-location" placeholder="Lieu">

      <label for="event-date">Date :</label>
      <input #eventDateInput type="text" id="event-date" class="input-nav" name="event-date" placeholder="dd/mm/yyyy" required>

      <label for="event-price">Prix :</label>
      <input #eventPriceInput type="number" id="event-price" class="input-nav" name="event-price" placeholder="Prix">

      <label for="event-description">Description :</label>
      <input #eventDescriptionInput id="event-description" class="input-nav" name="event-description" placeholder="Description">

      <label for="event-poster">Affiche :</label>
      <input #eventPosterInput type="text" id="event-poster" class="input-nav" name="event-poster" placeholder="URL de l'affiche">

      <button class="primary-button" (click)="createEvent()">Créer l'évènement</button>
    </form>
  </div>
</div>
