import { Component } from '@angular/core';
import {EventService} from "../services/event.service";
import {ActivatedRoute} from "@angular/router";
import {Event} from "../models/event.model";
import format from 'date-fns/format';
import {fr} from "date-fns/locale";
import {OrgaService} from "../services/orga.service";

@Component({
  selector: 'app-event-detail',
  templateUrl: './event-detail.component.html',
  styleUrls: ['./event-detail.component.css']
})
export class EventDetailComponent {
  ID = 0;
  name='';
  lieu='';
  description = '';
  date='';
  orga = '';
  orga_id=0;

  constructor(private route: ActivatedRoute, private eventService: EventService, private orgaService: OrgaService) {

  }

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      // @ts-ignore
      this.ID = +params.get('id');
      this.eventService.findById(this.ID).subscribe((data) => {
        this.description = data.description;
        this.lieu = data.lieu;
        const date = new Date(data.date.substring(0, 9));
        const formattedDate = format(date, 'EEEE dd LLLL yyyy', { locale: fr });
        this.date = formattedDate;
        this.name = data.name;
        this.orgaService.findById(data.orga).subscribe((dataorga) => {
          this.orga = dataorga.pseudo;
        });
      });
    });
  }
}

<div class="event-detail-content">
<img class= "resize rounded" src="../assets/posters/organik-affiche.jpg" alt="Affiche">
  <button class="round-button" (click)="ButtonParticipate($event, event.id)">Me désinscrire</button>
  <p class="event-title center">{{name}}</p>
  <div class="card rounded">
  <p class="event-information">Lieu: {{lieu}}</p>
    <br>
  <p class="event-information">Organisateur: <a class="event-planner" [routerLink]="['/organizer', orga_id]">{{orga}}</a></p>
    <br>
  <p class="date uppercase">Date: {{date}}</p>
  </div>
  <p class="title">Description</p>
  <div></div>
  <p class="event-description center">{{description}}</p>
</div>
