import { Component } from '@angular/core';

@Component({
  selector: 'app-connexion',
  templateUrl: './connexion.component.html',
  styleUrls: ['./connexion.component.css']
})
export class ConnexionComponent {

}
<div class="form-container">
  <div class="tabs">
    <button class="tab-button primary-button" id="login-tab">Connexion</button>
    <button class="tab-button primary-button" id="register-tab">S'inscrire</button>
  </div>
  <div class="tab-content" id="login-content">
    <form>
      <h2>Connexion</h2>

      <label for="login-username">Nom d'utilisateur :</label>
      <input type="text" id="login-username" class= "input-nav" name="login-username" placeholder="Nom d'utilisateur" required>

      <label for="login-password">Mot de passe :</label>
      <input type="password" id="login-password" class= "input-nav" name="login-password" placeholder="Mot de passe" required>

      <button class="primary-button">Se connecter</button>
    </form>
  </div>
  <div class="tab-content" id="register-content">
    <form>
      <h2>Inscription</h2>

      <label for="register-username">Nom d'utilisateur :</label>
      <input type="text" id="register-username" name="register-username" placeholder="Nom d'utilisateur" required>

      <label for="register-password">Mot de passe :</label>
      <input type="password" id="register-password" name="register-password" placeholder="Mot de passe" required>

      <button class="primary-button">S'inscrire</button>
    </form>
  </div>


  <script>
    document.getElementById('login-tab').addEventListener('click', function() {
      document.getElementById('login-content').style.display = 'block';
      document.getElementById('register-content').style.display = 'none';
    });

    document.getElementById('register-tab').addEventListener('click', function() {
      document.getElementById('login-content').style.display = 'none';
      document.getElementById('register-content').style.display = 'block';
    });
  </script>
