import { Component } from '@angular/core';
import {CardEventComponent} from "../card-event/card-event.component";

@Component({
  selector: 'app-home-events',
  templateUrl: './home-events.component.html',
  styleUrls: ['./home-events.component.css']
})
export class HomeEventsComponent {
  itemsVisibles: number = 10; // Commencez par afficher les 2 premiers éléments

  afficherPlus() {
    this.itemsVisibles += 2; // Augmentez le nombre d'éléments visibles de 2
  }
}
<section id="main">

<h1>Évènements plus populaires</h1>

<div class="card-grid">
  <app-card-event [hidden]="2>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="2>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="4>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="4>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="6>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="6>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="8>=itemsVisibles"></app-card-event>
  <app-card-event [hidden]="8>=itemsVisibles"></app-card-event>
</div>

  <button (click)="afficherPlus()">Afficher plus</button>
</section>
