import { Component } from '@angular/core';
import {map, Observable} from "rxjs";
import {Event} from "../models/event.model";
import {ActivatedRoute} from "@angular/router";
import {OrgaService} from "../services/orga.service";
import {EventService} from "../services/event.service";

@Component({
  selector: 'app-my-events',
  templateUrl: './my-events.component.html',
  styleUrls: ['./my-events.component.css']
})
export class MyEventsComponent {

  eventList : Event[]=[];
  constructor(private _route: ActivatedRoute, private eventService: EventService) {}

  ngOnInit() {
    this.eventService.findAll().subscribe((events: Event[]) => {
        this.eventList = events;
      }
    );
  }
  onButtonClick(){
    console.log("clique");
  }

}
<section id="main">

  <h1>My Events</h1>
  <div class="column-center">
    <button class="round-button" *ngFor="let event of eventList | async" (click)="onButtonClick()">Click</button>
    <app-card-event *ngFor="let event of eventList | async"  [id]=event.id [lieu]=event.lieu [theme]=event.theme [id_orga]=event.orga [name]=event.name [prix]="event.prix"></app-card-event>
  </div>

</section>
