import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  isConnected: boolean = false;
  username: string = '';
  ngOnInit() {
    this.connexionService.isConnected$.subscribe((isConnected) => {
      this.isConnected = isConnected;

      // Si connecté, récupérer le nom d'utilisateur
      if (isConnected) {
        this.username = this.connexionService.getUsername();
      }
    });
  }
}
<div class="navbar">
  <a class="app-name" routerLink="">TrackEvent</a>
  <div class="user-input">
    <input type="text" id="user-input" class="input-nav" placeholder="Rechercher...">
  </div>
  <nav>
    <ul>
      <li><a href="#amis">Amis</a></li>
      <li><a href="#">Events</a></li>
      <li><a class = "active" routerLink="connexion">Connexion / Inscription</a></li>
    </ul>
  </nav>
</div>
