import {Component, Input} from '@angular/core';
import {ConnexionService} from "../services/connexion.service";

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  isConnected: boolean = false;
  @Input() username: string = '';

  constructor(private connexionService: ConnexionService) {}
  ngOnInit() {
    this.connexionService.isConnected.subscribe((isConnected) => {
      this.isConnected = isConnected;
      if (isConnected) {
        this.username = this.connexionService.username;
      }
    });
  }
}
<div class="navbar">
  <a class="app-name" routerLink="">TrackEvent</a>
  <div class="user-input">
    <input type="text" id="user-input" class="input-nav" placeholder="Rechercher...">
  </div>
  <nav>
    <ul>
      <li *ngIf="username !== ''"><a routerLink="">Amis</a></li>
      <li *ngIf="username !== ''"><a routerLink="myevents">Mes Events</a></li>
      <li *ngIf="username == ''"><a class = "active" routerLink="connexion">Connexion / Inscription</a></li>

      <li *ngIf="username !== ''"><a class = "active">Bienvenue {{username}} !</a></li>
      <li *ngIf="username !== ''" class="logout"><a>Déconnexion</a></li>
    </ul>
  </nav>
</div>
