import { Component } from '@angular/core';

@Component({
  selector: 'app-event-detail',
  templateUrl: './event-detail.component.html',
  styleUrls: ['./event-detail.component.css']
})
export class EventDetailComponent {
  description: String ="";
}
<div class="event-detail-content">
<img class= "resize rounded" src="../assets/posters/organik-affiche.jpg" alt="Affiche">

  <p class="event-title center">Organik: Open Air</p>
  <div class="card rounded">
  <p class="event-information">Lieu: Kilomètre 25</p>
    <br>
  <p class="event-information">Organisateur: <a class="event-planner" [routerLink]="['/organizer', 1]">Organik</a></p>
    <br>
  <p class="date uppercase"><span>17 Sep</span> | <span>23h00-7h00</span></p>
  </div>
  <p class="title">Description</p>
  <p class="event-description center">{{description}}</p>
</div>
