import {Component, Input} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {map, Observable} from "rxjs";
import {OrgaService} from "../services/orga.service";
import {Orga} from "../models/orga.model";

@Component({
  selector: 'app-card-event',
  templateUrl: './card-event.component.html',
  styleUrls: ['./card-event.component.css']
})
export class CardEventComponent {
  @Input() name = "";
  @Input() lieu = "";
  @Input() id = 0;
  @Input() description = "";
  @Input() prix = 0;
  @Input() date = "";
  @Input() id_orga = 0;
  @Input() name_orga = "";
  @Input() theme = "";
  @Input() affiche = "";

  constructor(private orgaService: OrgaService) {}
  ngOnInit(): void {
    this.orgaService.findById(this.id).subscribe((data) => {
      this.name_orga = data.pseudo;
    });
  }
}
<div class="card rounded">
  <a [routerLink]="['/event', id]">
  <img class= "resize rounded" src="../assets/posters/organik-affiche.jpg" alt="Affiche">
  </a>
    <div class="card-margin">
    <h1>{{name}}</h1>
      <a class="localisation Link" [routerLink]="['/organizer', id]">Par <span class="uppercase large_white">{{name_orga}}</span></a>
    <p class="localisation uppercase">{{lieu}}</p>
    <p class="date uppercase"><span class="inherit-color">{{date}}</span> | <span class="inherit-color">23h00-7h00</span></p>
    <div>
      <p class="music-card">{{theme}}</p>
    </div>
  </div>
</div>
